begin
wwv_flow_imp_shared.create_plugin(
 p_id=>wwv_flow_imp.id(21419428280774344247)
,p_plugin_type=>'ITEM TYPE'
,p_name=>'ANGOOTI.CAPTCHA'
,p_display_name=>'Apex Captcha'
,p_supported_component_types=>'APEX_APPLICATION_PAGE_ITEMS'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('ITEM TYPE','ANGOOTI.CAPTCHA'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'g_main_id   varchar2(4000);',
'',
'---------------------------------------------------------------------------',
'-- set_collection',
'---------------------------------------------------------------------------',
'procedure set_collection(p_code in varchar2) is',
'    v_seq number;',
'begin',
'    apex_collection.create_or_truncate_collection(''rmcaptcha_collection'');   ',
'    v_seq := apex_collection.add_member(',
'        p_collection_name => ''rmcaptcha_collection'',',
'        p_c001            => p_code',
'    ); ',
'end set_collection;',
'',
'---------------------------------------------------------------------------',
'-- get_collection',
'---------------------------------------------------------------------------',
'function get_collection return varchar2 is',
'    v_ret   varchar2(4000);',
'begin',
'    for i in ( select c001 ret from apex_collections where collection_name=''RMCAPTCHA_COLLECTION'' ) loop',
'        v_ret := i.ret;',
'    end loop; ',
'',
'    return v_ret;',
'end get_collection;',
'',
'---------------------------------------------------------------------------',
'-- create_code',
'---------------------------------------------------------------------------',
'function create_code(p_characters varchar2, p_length number) return varchar2 is',
'    v_res varchar2(4000);',
'begin',
'    select substr(listagg(substr(p_characters, level, 1)) within group(order by dbms_random.value), 1, p_length)',
'        into v_res',
'    from dual',
'    connect by level <= length(p_characters);',
'',
'    return v_res;',
'end create_code;',
'',
'-------------------------------------------------------------------------------',
'-- show_index',
'------------------------------------------------------------------------------- ',
'procedure show_index(v_index in number) as',
'    v_code          varchar2(4000) := lower(get_collection());',
'    v_char          char(1);',
'    v_blob          blob;',
'    v_filename      varchar2(4000);',
'begin',
'    v_char := substr(v_code, v_index, 1);',
'    for i in (select FILE_CONTENT from APEX_APPL_PLUGIN_FILES where PLUGIN_NAME=''ANGOOTI.CAPTCHA'' and FILE_NAME=v_char || ''.png'') loop',
'        v_blob := i.FILE_CONTENT;',
'    end loop;',
'    if v_blob is not null then',
'        v_filename := ''rmcaptcha_file-'' || replace(regexp_replace(to_char(sysdate,''yyyy/mm/dd HH24:MI:SS''),''/|-|:'',''''),'' '');',
'        sys.htp.init;',
'        sys.owa_util.mime_header(''application/octet-stream'',false,''utf-8'');',
'        sys.htp.p(''content-length: '' || sys.dbms_lob.getlength(v_blob));',
'        sys.htp.p(''content-disposition: inline; filename="'' || v_filename || ''.png"'' );',
'        sys.owa_util.http_header_close;',
'        sys.wpg_docload.download_file(v_blob);',
'    end if;',
'    apex_application.stop_apex_engine;',
'end show_index;',
'',
'---------------------------------------------------------------------------',
'-- create_style',
'---------------------------------------------------------------------------',
'function create_style(p_code varchar2, p_plugin in apex_plugin.t_plugin) return varchar2 is',
'    v_char_template varchar2(4000); ',
'    v_ret           varchar2(32000); ',
'    v_char          char(1);',
'    v_img           varchar2(4000);',
'    v_blob          blob; ',
'    v_clob          clob;   ',
'    v_path          varchar2(4000);',
'begin',
'    for i in 1..length(p_code) loop',
'        v_char := substr(p_code,i,1);',
'        v_path := ''wwv_flow.show?p_flow_id=#FLOW_ID#&p_flow_step_id=#STEP_ID#&p_instance=#INSTANCE_ID#&x01=show_image&x02=#INDEX#&p_request=PLUGIN=#IDENT_CODE#&time='' || trunc(dbms_random.value(1111111111,9999999999));',
'        v_path := replace(v_path, ''#FLOW_ID#'', APEX_APPLICATION.G_FLOW_ID);',
'        v_path := replace(v_path, ''#STEP_ID#'', APEX_APPLICATION.G_FLOW_STEP_ID);',
'        v_path := replace(v_path, ''#INSTANCE_ID#'', v(''APP_SESSION''));',
'        v_path := replace(v_path, ''#INDEX#'', i);',
'        v_path := replace(v_path, ''#IDENT_CODE#'', APEX_UTIL.URL_ENCODE(apex_plugin.get_ajax_identifier));',
'        v_char_template  := ''<img src="#PATH#" />'';',
'        v_char_template  := replace(v_char_template, ''#PATH#'', v_path);',
'        v_ret := v_ret || v_char_template;',
'    end loop;',
'',
'    return v_ret;',
'end create_style;',
'',
'---------------------------------------------------------------------------',
'-- write_captcha',
'---------------------------------------------------------------------------',
'procedure write_captcha(p_item in apex_plugin.t_item, p_plugin in apex_plugin.t_plugin) is',
'    v_type          char(1) := p_item.attribute_01;',
'    v_length        number := p_item.attribute_02;',
'    v_characters    varchar2(4000);',
'    v_code          varchar2(4000);',
'    v_code_style    varchar2(32000);',
'    v_css           varchar2(32000);',
'    v_js            varchar2(32000);',
'    v_out           varchar2(32000);',
'begin',
'    if v_type = ''C'' then',
'        v_characters := ''0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ'';',
'    elsif v_type = ''L'' then',
'        v_characters := ''ABCDEFGHIJKLMNOPQRSTUVWXYZ'';',
'    elsif v_type = ''N'' then',
'        v_characters := ''0123456789'';',
'    end if;',
'',
'    v_code := create_code(v_characters, v_length);',
'    set_collection(v_code);',
'',
'    v_code_style := create_style(v_code, p_plugin);',
'',
'    v_css := ''##g_main_id#{',
'                width: #HEIGHT#px;',
'                height: 66px;',
'                display: inline-block;',
'                padding: 0px 0px 0px 0px;',
'                border-radius: 6px;',
'                padding-top: 0px;',
'                margin: 0px 0px 6px 0px;',
'                border: 1px solid #dbd8d8;',
'                background: URL("#BG_IMG#");',
'            }'';',
'    v_css := replace(v_css, ''#g_main_id#'', g_main_id);',
'    v_css := replace(v_css, ''#HEIGHT#'', LENGTH(v_code)*28);',
'    v_css := replace(v_css, ''#BG_IMG#'', v(''APP_IMAGES/'') || p_plugin.file_prefix || ''bg.png'' );',
'    apex_css.add (',
'        p_css => v_css,',
'        p_key => g_main_id',
'    );',
'',
'    v_out := ''',
'        <div id="#g_main_id#">',
'            <center>',
'            #v_code_style#',
'            </center>',
'        </div>',
'        <span id="spn_rmcaptcha_refresh_#g_main_id#" title="New image" style="margin-top: 50px; cursor: pointer" onclick="rmcaptcha_refresh_#g_main_id#()" aria-hidden="true" class="t-Icon fa fa-refresh"></span>',
'        <br>',
'        <input type="text" placeholder="#placeholder#" name="#name#" id="#id#" class="text_field apex-item-text" maxlength="#length#" style="margin: 0px 0px 8px 0px;text-align: center;width: 120px;" />',
'    '';',
'    v_css := replace(v_css, ''#g_main_id#'', g_main_id);',
'    v_out := replace(v_out, ''#g_main_id#'', g_main_id);',
'    v_out := replace(v_out, ''#v_code_style#'', v_code_style);',
'    v_out := replace(v_out, ''#name#'', p_item.name);',
'    v_out := replace(v_out, ''#id#'', p_item.name);',
'    v_out := replace(v_out, ''#length#'', v_length);',
'    v_out := replace(v_out, ''#placeholder#'', p_item.attribute_04);',
'    htp.p(v_out);',
'    ',
'end write_captcha;',
'',
'---------------------------------------------------------------------------',
'-- write_captcha',
'---------------------------------------------------------------------------',
'procedure write_ajax_function(p_item in apex_plugin.t_item) is',
'    v_type          char(1) := p_item.attribute_01;',
'    v_length        number := p_item.attribute_02;',
'    v_characters    varchar2(4000);',
'    v_code          varchar2(4000);',
'    v_code_style    varchar2(4000);',
'    v_css           varchar2(32000);',
'    v_js            varchar2(32000);',
'    v_out           varchar2(32000);',
'    v_identifier    varchar2(32000);',
'begin',
'    v_identifier := apex_plugin.get_ajax_identifier;',
'    v_js := ''',
'        function rmcaptcha_refresh_#g_main_id#() {',
'            var v_url = apex.server.pluginUrl("#v_identifier#");',
'            lSpinner$ = apex.util.showSpinner( $( "##g_main_id#" ) );',
'            $("#spn_rmcaptcha_refresh_#g_main_id#").hide();',
'            $.get(v_url, function(data, status){',
'                $("##g_main_id#").html("<center>"+data+"</center>");',
'                lSpinner$.remove();',
'                $("#spn_rmcaptcha_refresh_#g_main_id#").show();',
'            });',
'        }    ',
'    '';',
'    v_js  := replace(v_js, ''#g_main_id#'', g_main_id);',
'    v_js  := replace(v_js, ''#v_identifier#'', v_identifier);',
'    APEX_JAVASCRIPT.ADD_INLINE_CODE (',
'        p_code => v_js,',
'        p_key => ''rmcaptcha_refresh_'' || g_main_id',
'    ); ',
'end write_ajax_function;',
'',
'---------------------------------------------------------------------------',
'-- Render',
'---------------------------------------------------------------------------',
'PROCEDURE Render (',
'    p_item  IN apex_plugin.t_item,',
'    p_plugin IN apex_plugin.t_plugin, ',
'    p_param  IN apex_plugin.t_item_render_param, ',
'    p_result IN OUT nocopy apex_plugin.t_item_render_result) ',
'IS ',
'BEGIN    ',
'    g_main_id := ''rm_'' || lower(p_item.name);',
'    write_captcha(p_item, p_plugin);',
'    write_ajax_function(p_item);',
'END render; ',
'',
'---------------------------------------------------------------------------',
'-- ajax_request',
'---------------------------------------------------------------------------',
'procedure ajax_request (',
'    p_item   in            apex_plugin.t_item,',
'    p_plugin in            apex_plugin.t_plugin,',
'    p_param  in            apex_plugin.t_item_ajax_param,',
'    p_result in out nocopy apex_plugin.t_item_ajax_result',
')',
'IS',
'    v_type              char(1) := p_item.attribute_01;',
'    v_length            number := p_item.attribute_02;',
'    v_characters        varchar2(4000);',
'    v_code              varchar2(4000);',
'    v_code_style        varchar2(32000);',
'    v_out               varchar2(32000);',
'    v_function_type     varchar2(4000) := APEX_APPLICATION.G_X01;',
'    v_index             varchar2(4000) := APEX_APPLICATION.G_X02;',
'BEGIN',
'    if v_function_type = ''show_image'' then',
'        htp.p(''v_function_type = '' || v_function_type);',
'        htp.p(''v_index = '' || v_index);',
'        show_index(v_index);',
'    else',
'        if v_type = ''C'' then',
'            v_characters := ''0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ'';',
'        elsif v_type = ''L'' then',
'            v_characters := ''ABCDEFGHIJKLMNOPQRSTUVWXYZ'';',
'        elsif v_type = ''N'' then',
'            v_characters := ''0123456789'';',
'        end if;',
'        v_code := create_code(v_characters, v_length);',
'        set_collection(v_code);',
'        v_code_style := create_style(v_code, p_plugin);',
'        htp.p(v_code_style);',
'    end if;',
'END ajax_request;',
'',
'---------------------------------------------------------------------------',
'-- validate_item',
'---------------------------------------------------------------------------',
'procedure validate_item (',
'    p_item   in            apex_plugin.t_item,',
'    p_plugin in            apex_plugin.t_plugin,',
'    p_param  in            apex_plugin.t_item_validation_param,',
'    p_result in out nocopy apex_plugin.t_item_validation_result )',
'IS',
'    v_message   varchar2(4000) := p_item.attribute_03;',
'BEGIN',
'    if nvl(upper(v(p_item.name)),0) != get_collection() then',
'        p_result.message := v_message;',
'    end if;',
'END;',
'',
''))
