'CREATE OR REPLACE EDITIONABLE PACKAGE BODY "SORTEO_CONCEPTO" is',
'procedure alerta_puntos is',
'        cursor CUR_ALERTA is',
unistr('                    select distinct a.id_cliente,c.nombre,c.email,c.nombre || '': Tenes Puntos que vencer\00E1n en los pr\00F3ximos d\00EDas, no los pierdas y canjealos por espectaculares premios. '' mensaje from ('),
'                    select * from bolsa_puntaje ',
'                    where saldo_puntos > 0 and fecha_caducidad = trunc(sysdate) + (select cantidad_dia from parametro_dia)) a',
'                    join cliente c on c.id_cliente = a.id_cliente;  ',
'begin',
'    ',
'    FOR CUR IN CUR_ALERTA LOOP',
'        BEGIN',
'         apex_mail.send(',
'         p_to => CUR.email, ',
'         p_from => ''wilsonvelazar3@fpuna.edu.py'', ',
'         p_subj => ''PUNTAJES POR VENCER'',',
'         p_body => CUR.mensaje );',
'        END;',
'    END LOOP;',
'end;',
'procedure sorteo is',
'c number := 0;',
'email_sorteo varchar2(200);',
'nombre_sorteo varchar2(100);',
'begin   ',
'    select 1 into c from parametro_sorteo where trunc(FECHA_SORTEO) = trunc(sysdate);',
'    if c = 1 then',
'        SELECT email, nombre',
'        INTO email_sorteo, nombre_sorteo',
'        FROM (',
'        select p.id_cliente,c.nombre,c.email,sum(saldo_puntos),c.nombre || '' has ganado el sorteo''  as mensaje_2',
'        from bolsa_puntaje p',
'        join cliente c on c.id_cliente = p.id_cliente ',
'        where fecha_caducidad >= trunc(sysdate) and saldo_puntos > 0',
'        group by p.id_cliente,c.nombre,c.email',
'        having sum(saldo_puntos) > (select minimo_punto from parametro_sorteo) and sum(saldo_puntos) < (select maximo_punto from parametro_sorteo)',
'        ) a ORDER BY DBMS_RANDOM.value FETCH NEXT 1 ROWS ONLY;',
'        BEGIN',
'         apex_mail.send(',
'         p_to => email_sorteo, ',
'         p_from => ''wilsonvelazar3@fpuna.edu.py'', ',
'         p_subj => ''SORTEO'',',
unistr('         p_body => nombre_sorteo || '' \00A1Te has ganado el sorteo para canjear algun concepto!'' );'),
'        END;',
'    end if;',
'end;',
'procedure principal is',
'begin',
'    SORTEO_CONCEPTO.alerta_puntos;',
'	SORTEO_CONCEPTO.sorteo;',
'end;',
'end "SORTEO_CONCEPTO";',
