'',
'CREATE OR REPLACE EDITIONABLE PROCEDURE "PR_ASIGNACION_PUNTO" (P_ID_PERSONA IN NUMBER,',
'                                                P_ID_PUNTAJE IN NUMBER,',
'                                                P_MONTO_OPERACION IN NUMBER)',
'IS',
'V_RANGO_UNO_DE_0 NUMBER := 0;',
'V_RANGO_DOS_A_199999 NUMBER := 0;',
'V_RANGO_UNO_DE_200000 NUMBER := 0;',
'V_RANGO_DOS_A_499999 NUMBER := 0;',
'V_RANGO_UNO_DE_500000 NUMBER := 0;',
'V_RANGO_DOS_A_N NUMBER := 0;',
'V_PUNTO NUMBER := 0;',
'V_FECHA_FIN_1 DATE;',
'V_FECHA_FIN_2 DATE;',
'V_FECHA_FIN_3 DATE;',
'V_FECHA_VENCIMIENTO DATE;',
'BEGIN',
'   BEGIN',
'    select RANGO_UNO RANGO_UNO_DE_0,',
'           RANGO_DOS RANGO_DOS_A_199999,',
'           FECHA_FIN',
'    INTO V_RANGO_UNO_DE_0,',
'        V_RANGO_DOS_A_199999,',
'        V_FECHA_FIN_1',
'    from VENCIMIENTO_PUNTAJE',
'    WHERE ID_VENCIMIENTO = 1;',
'   EXCEPTION WHEN NO_DATA_FOUND THEN',
'     NULL;',
'   END;',
'   BEGIN',
'    select RANGO_UNO RANGO_UNO_DE_200000,',
'            RANGO_DOS RANGO_DOS_A_499999,',
'            FECHA_FIN',
'    INTO V_RANGO_UNO_DE_200000,',
'         V_RANGO_DOS_A_499999,',
'         V_FECHA_FIN_2',
'    from VENCIMIENTO_PUNTAJE',
'    WHERE ID_VENCIMIENTO = 21;',
'   EXCEPTION WHEN NO_DATA_FOUND THEN',
'     NULL;',
'   END; ',
'BEGIN',
'select RANGO_UNO RANGO_UNO_DE_500000,',
'       RANGO_DOS RANGO_DOS_A_N,',
'       FECHA_FIN',
'INTO V_RANGO_UNO_DE_500000,',
'     V_RANGO_DOS_A_N,',
'     V_FECHA_FIN_3',
'from VENCIMIENTO_PUNTAJE',
'WHERE ID_VENCIMIENTO = 41;',
'EXCEPTION WHEN NO_DATA_FOUND THEN',
'     NULL;',
'   END;',
' IF P_MONTO_OPERACION BETWEEN V_RANGO_UNO_DE_0 AND V_RANGO_DOS_A_199999 THEN',
'    V_PUNTO := TRUNC(P_MONTO_OPERACION/50000);',
'    V_FECHA_VENCIMIENTO := V_FECHA_FIN_1;',
' ELSIF P_MONTO_OPERACION BETWEEN V_RANGO_UNO_DE_200000 AND V_RANGO_DOS_A_499999 THEN',
'    V_PUNTO := TRUNC(P_MONTO_OPERACION/30000);',
'    V_FECHA_VENCIMIENTO := V_FECHA_FIN_2;',
' ELSIF P_MONTO_OPERACION >= V_RANGO_UNO_DE_500000 THEN',
'    V_PUNTO := TRUNC(P_MONTO_OPERACION/20000);',
'    V_FECHA_VENCIMIENTO := V_FECHA_FIN_3;',
' END IF;',
'    UPDATE BOLSA_PUNTAJE ',
'    SET PUNTAJE_ASIGNADO = V_PUNTO,',
'        FECHA_CADUCIDAD = V_FECHA_VENCIMIENTO,',
'        FECHA_ASIGNACION = TRUNC(SYSDATE),',
'        SALDO_PUNTOS = V_PUNTO',
'    WHERE ID_BOLSA_PUNTAJE = P_ID_PUNTAJE',
'    AND ID_CLIENTE = P_ID_PERSONA;',
'    COMMIT;',
'END;',
'/',
'',
'CREATE OR REPLACE EDITIONABLE PROCEDURE "PR_ASIGNAR_BOLSA_PUNTO" (P_ID_CLIENTE IN NUMBER,',
'                                         P_PUNTO_UTILIZADO IN OUT NUMBER,',
'                                         P_ID_CABECERA IN NUMBER)',
'IS',
'V_SALDO_PUNTOS NUMBER := 0;',
'    CURSOR  CUR_BOLSA IS',
'            SELECT SALDO_PUNTOS, ',
'                    ID_CLIENTE, ',
'                    ID_BOLSA_PUNTAJE',
'           FROM BOLSA_PUNTAJE ',
'           WHERE ID_CLIENTE = P_ID_CLIENTE;',
'BEGIN',
'    SELECT SUM(SALDO_PUNTOS)',
'    INTO V_SALDO_PUNTOS',
'    FROM BOLSA_PUNTAJE ',
'    WHERE ID_CLIENTE = P_ID_CLIENTE;',
'    IF P_PUNTO_UTILIZADO <= V_SALDO_PUNTOS THEN',
'        FOR CUR IN CUR_BOLSA LOOP',
'          IF CUR.SALDO_PUNTOS != 0 THEN',
'             IF P_PUNTO_UTILIZADO  >= CUR.SALDO_PUNTOS THEN',
'                INSERT INTO DET_USO_PUNTAJE(ID_USO_CAB,PUNTAJE_UTILIZADO,ID_BOLSA_PUNTAJE)',
'                   VALUES(P_ID_CABECERA,CUR.SALDO_PUNTOS,CUR.ID_BOLSA_PUNTAJE);',
'                   ',
'                   UPDATE BOLSA_PUNTAJE',
'                   SET PUNTAJE_UTILIZADO = CUR.SALDO_PUNTOS,',
'                       SALDO_PUNTOS = SALDO_PUNTOS - CUR.SALDO_PUNTOS',
'                    WHERE ID_CLIENTE = P_ID_CLIENTE',
'                    AND ID_BOLSA_PUNTAJE = CUR.ID_BOLSA_PUNTAJE;',
'                    commit;    ',
'                    P_PUNTO_UTILIZADO := P_PUNTO_UTILIZADO - CUR.SALDO_PUNTOS ; ',
'            ELSE',
'                   INSERT INTO DET_USO_PUNTAJE(ID_USO_CAB,PUNTAJE_UTILIZADO,ID_BOLSA_PUNTAJE)',
'                   VALUES(P_ID_CABECERA,P_PUNTO_UTILIZADO,CUR.ID_BOLSA_PUNTAJE);',
'',
'                   UPDATE BOLSA_PUNTAJE',
'                   SET PUNTAJE_UTILIZADO = P_PUNTO_UTILIZADO,',
'                       SALDO_PUNTOS = CUR.SALDO_PUNTOS - P_PUNTO_UTILIZADO ',
'                    WHERE ID_CLIENTE = P_ID_CLIENTE',
'                    AND ID_BOLSA_PUNTAJE = CUR.ID_BOLSA_PUNTAJE;',
'',
'                    commit;',
'                    P_PUNTO_UTILIZADO := 0 ;',
